#!/bin/bash  NDKRROOT=/cygdrive/f/android/android-ndk-r8 PREBUILT=$NDKRROOT/toolchains/arm-linux-androideabi-4.4.3/prebuilt/windows PLATFORM=$NDKRROOT/platforms/android-8/arch-arm export PATH=$PATH:$PREBUILT/binFFCONFIG_COMMON='  --enable-version3  --disable-gpl  --disable-nonfree  --disable-everything  --enable-decoder=h264  --enable-armv5te  --disable-ffmpeg  --disable-ffplay  --disable-ffprobe  --disable-ffserver  --disable-avfilter  --disable-avformat   --disable-avdevice   --disable-swresample   --disable-swscale  ' ./configure --prefix=$PWD/_install_arm $FFCONFIG_COMMON \     --arch=arm \     --target-os=linux \     --enable-small \     --disable-debug     --disable-logging     --enable-cross-compile \     --sysroot=$PLATFORM/user \     --sysinclude=$PLATFORM/usr/include \     --cross-prefix=arm-linux-androideabi- \     --extra-ldflags=" -L$PLATFORM/usr/lib -nostdlib  -lc -lm -ldl" \     --extra-cflags="-I$PLATFORM/usr/include -O3"
